import { useSelector } from "react-redux";
import Job from "./Job";

const Favorites = () => {
  const favJobs = useSelector((state) => {
    return state.favorite.companies;
  });
  console.log(favJobs);
  return (
    <>
      <h2>your favovorite companies:</h2>
      {favJobs.map((job) => (
        <Job key={job._id} data={job} />
      ))}
    </>
  );
};
export default Favorites;
