const initialState = {
  favorite: {
    companies: [],
    jobs: [],
  },
  some: null,
};

const mainReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SAVE_FAV":
      return { ...state, companies: [...state.companies, action.payload] };

    default:
      return state;
  }
};
export default mainReducer;
